import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { acaiSchema } from '../../validation';
import { useDispatch } from 'react-redux';
import { setPedido } from '../../features/pedido/pedidoSlice';
import { useNavigate } from 'react-router-dom';
import { fetchProdutos } from '../../services/mockApi';
import Footer from '../../components/Footer/Footer';
import RadioTamanho from '../../components/Home/RadioTamanho';
import RadioFruta from '../../components/Home/RadioFruta';
import CheckboxComplementos from '../../components/Home/CheckboxComplementos';
import BotaoVoltar from '../../components/BotaoVoltar';
import produtoImg from '../../assets/img/Banner.png';
import '../../components/Home/Home.css';

type FormValues = {
    tamanho: string;
    sabor: string;
    complementos: Record<string, boolean>;
};

export const Home = () => {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const [quantidade, setQuantidade] = useState(1);
    const [valorTotal, setValorTotal] = useState(10); // Valor inicial arbitrário
    const [etapaAtual, setEtapaAtual] = useState(1);
    const [tamanhos, setTamanhos] = useState([]);
    const [sabores, setSabores] = useState([]);
    const [complementos, setComplementos] = useState([]);

    const { register, handleSubmit, watch, formState: { errors } } = useForm<FormValues>({
        resolver: zodResolver(acaiSchema),
    });

    useEffect(() => {
        const loadData = async () => {
            const produtosData = await fetchProdutos();
            setTamanhos(produtosData.filter(produto => produto.tipo === 'tamanho'));
            setSabores(produtosData.filter(produto => produto.tipo === 'sabor'));
            setComplementos(produtosData.filter(produto => produto.tipo === 'complemento'));
        };

        loadData();
    }, []);

    const onSubmitData = (data: FormValues) => {
        let totalValor = tamanhos
            .filter(tamanho => tamanho.nome === data.tamanho)
            .reduce((acc, curr) => acc + parseFloat(curr.valor.replace(',', '.')), 0);

        Object.keys(data.complementos).forEach(key => {
            if (data.complementos[key]) {
                const complementoValor = complementos
                    .filter(complemento => complemento.nome.toLowerCase() === key.toLowerCase())
                    .reduce((acc, curr) => acc + parseFloat(curr.valor.replace(',', '.')), 0);

                totalValor += complementoValor;
            }
        });

        totalValor *= quantidade;

        dispatch(setPedido({
            ...data,
            valorTotal: totalValor.toFixed(2),
        }));

        navigate('/pedido');
    };    

    const avancarParaProximaEtapa = () => {
        if (etapaAtual < 3) {
            setEtapaAtual(etapaAtual + 1);
        } else {
            handleSubmit(onSubmitData)();
        }
    };

    const voltarEtapa = () => {
        if (etapaAtual > 1) {
            setEtapaAtual(etapaAtual - 1);
        }
    };

    return (
        <section>
            <div id='produto' className='container'>
                <BotaoVoltar onClick={voltarEtapa} />
                <img src={produtoImg} alt="produto açaí" />
            </div>
            <form className='container' onSubmit={handleSubmit(onSubmitData)}>
                {etapaAtual === 1 && <RadioTamanho register={register} tamanhos={tamanhos} />}
                {etapaAtual === 2 && <RadioFruta register={register} sabores={sabores} />}
                {etapaAtual === 3 && <CheckboxComplementos register={register} complementos={complementos} />}
                <Footer
                    onSubmit={avancarParaProximaEtapa}
                    valorTotal={`R$ ${valorTotal.toFixed(2)}`}
                    quantidade={quantidade}
                    setQuantidade={setQuantidade}
                />
            </form>
        </section>
    );
};
