import React, { useState, useEffect } from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { acaiSchema } from '../../validation';
import { useDispatch } from 'react-redux';
import { setPedido } from '../../features/pedido/pedidoSlice';
import { useNavigate } from 'react-router-dom';
import './Home.css';
import Footer from '../../components/Footer';


type FormValues = {
    sabor: 'MORANGO' | 'BANANA' | 'KIWI';
    tamanho: 'PEQUENO' | 'MEDIO' | 'GRANDE';
    complemento: 'GRANOLA' | 'PAÇOCA' | 'LEITE NINHO' | 'NENHUM';
};

export const Home = () => {

    const navigate = useNavigate();
    const dispatch = useDispatch();
    const { register, handleSubmit, formState: { errors }, watch } = useForm<FormValues>({
        resolver: zodResolver(acaiSchema),
    });

    const [showFooter, setShowFooter] = useState(false);

    const watchFields = watch(["sabor", "tamanho", "complemento"]);

    const onSubmit: SubmitHandler<FormValues> = (data) => {
        dispatch(setPedido(data));
        navigate('/pedido');
    };

    const [valorTotal, setValorTotal] = useState(0);
    setValorTotal(valorTotal); 

    useEffect(() => {
        const allFieldsFilled = watchFields.every(field => field !== "");
        setShowFooter(allFieldsFilled); // Atualiza a visibilidade do Footer

        let valorTotal = 0;

        if (watchFields[1] === 'PEQUENO') valorTotal += 10;
        else if (watchFields[1] === 'MEDIO') valorTotal += 12;
        else if (watchFields[1] === 'GRANDE') valorTotal += 15;

        if (watchFields[2] !== 'NENHUM') valorTotal += 3;
    }, [watchFields]);


    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <div>
                <h1>Açaí Natural</h1>
                <label>Sabor:</label>
                <select {...register('sabor')}>
                    <option value="">Selecione um sabor</option>
                    <option value="MORANGO">Morango</option>
                    <option value="BANANA">Banana</option>
                    <option value="KIWI">Kiwi</option>
                </select>
                {errors.sabor && <p>O sabor é obrigatório.</p>}
            </div>
            <div>
                <label>Tamanho:</label>
                <select {...register('tamanho')}>
                    <option value="">Selecione um tamanho</option>
                    <option value="PEQUENO">Pequeno (300ml)</option>
                    <option value="MEDIO">Médio (500ml)</option>
                    <option value="GRANDE">Grande (700ml)</option>
                </select>
                {errors.tamanho && <p>O tamanho é obrigatório.</p>}
            </div>
            <div>
                <label>Complemento:</label>
                <select {...register('complemento')}>
                    <option value="">Selecione um complemento</option>
                    <option value="GRANOLA">Granola</option>
                    <option value="PAÇOCA">Paçoca</option>
                    <option value="LEITE NINHO">Leite Ninho</option>
                    <option value="NENHUM">Nenhum</option>
                </select>
                {errors.complemento && <p>O complemento é obrigatório.</p>}
            </div>
            <div id="footer">
                <div id="opcao-de-quantidade">
                    <button id="menos">-</button>
                    <div id="quantidade">2</div>
                    <button id="mais">+</button>
                </div>
                <button id="btn-avancar" type="submit">Avançar<span id="valor">R$ 18,00</span></button>
            </div>
            <Footer onSubmit={() => handleSubmit(onSubmit)()} valorTotal={`R$ ${valorTotal.toFixed(2)}`} />
            {showFooter && <Footer onSubmit={() => handleSubmit(onSubmit)()} valorTotal={"R$ 18,00"} />}
        </form>
    );
};
