import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { acaiSchema } from '../../validation';
import { useDispatch } from 'react-redux';
import { setPedido } from '../../features/pedido/pedidoSlice';
import { useNavigate } from 'react-router-dom';
import { fetchProdutos } from '../../services/mockApi';
import RadioTamanho from '../../components/Home/RadioTamanho';
import RadioFruta from '../../components/Home/RadioFruta';
import CheckboxComplementos from '../../components/Home/CheckboxComplementos';
import Footer from '../../components/Footer/Footer';
import BotaoVoltar from '../../components/BotaoVoltar';
import produtoImg from '../../assets/img/Banner.png';
import '../../components/Home/Home.css';

// Adicionando o tipo Produto, conforme já definido previamente.
interface Produto {
    nome: string;
    tipo: 'tamanho' | 'sabor' | 'complemento';
    valor?: string;
}

type FormValues = {
    tamanho: string;
    sabor: string;
    complementos: Record<string, boolean>;
};

export const Home = () => {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const [quantidade, setQuantidade] = useState(1);
    const [valorTotal, setValorTotal] = useState<number>(0); // Certifique-se de que valorTotal é um número
    const [etapaAtual, setEtapaAtual] = useState(1);
    const [produtos, setProdutos] = useState<Produto[]>([]); // Garanta que produtos é do tipo Produto[]

    const { register, handleSubmit, watch, formState: { errors }, getValues, setValue } = useForm<FormValues>({
        resolver: zodResolver(acaiSchema),
    });

    useEffect(() => {
        const loadData = async () => {
            const produtosData: Produto[] = await fetchProdutos();
            setProdutos(produtosData); // Assume que setProdutos atualiza o estado corretamente
            if (produtosData.length > 0) {
                const tamanhoInicial = produtosData.find((p: Produto) => p.tipo === 'tamanho')?.nome || '';
                setValue('tamanho', tamanhoInicial);
                // Após a inicialização, force uma atualização do valor total
                const formData = getValues(); // Obtenha os valores atuais do formulário
                calculateAndUpdateTotal(formData, quantidade);
            }
        };
        loadData();
    }, [setValue, getValues, quantidade]); // Adicione dependências faltantes se necessário

    const calculateAndUpdateTotal = () => {
        const currentValues = getValues();
        let totalValor = 0;
        // Implementação do cálculo do valor total com base nos valores atuais
        // e a quantidade de produtos
        setValorTotal(totalValor);
    };

    useEffect(() => {
        const subscription = watch(() => {
            calculateAndUpdateTotal();
        });
        return () => subscription.unsubscribe();
    }, [watch]);

    useEffect(() => {
        calculateAndUpdateTotal();
    }, [quantidade]);

    const onSubmitData = (data: FormValues) => {
        const totalValor = calculateAndUpdateTotal(data, quantidade);
        dispatch(setPedido({
            ...data,
            valorTotal: totalValor, // Certifique-se de que valorTotal é um número
        }));
        navigate('/pedido');
    };

    return (
        <section>
            {/* Marcação... */}
            <form className='container' onSubmit={handleSubmit(onSubmitData)}>
                {/* Componentes... */}
                <Footer
                    onSubmit={handleSubmit(onSubmitData)} // Correção: diretamente passar onSubmitData
                    valorTotal={`R$ ${valorTotal.toFixed(2)}`}
                    quantidade={quantidade}
                    setQuantidade={setQuantidade}
                />
            </form>
        </section>
    );
};
