import React, { useState, useEffect } from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { acaiSchema } from '../../validation';
import { useDispatch } from 'react-redux';
import { setPedido } from '../../features/pedido/pedidoSlice';
import { useNavigate } from 'react-router-dom';
import Footer from '../../components/Footer/Footer';
import RadioTamanho from '../../components/Home/RadioTamanho';
import RadioFruta from '../../components/Home/RadioFruta';
import CheckboxComplementos from '../../components/Home/CheckboxComplementos';
import '../../components/Home/Home.css';

type FormValues = {
    tamanho: 'PEQUENO' | 'MEDIO' | 'GRANDE';
    sabor: 'MORANGO' | 'BANANA' | 'KIWI';
    complementos: {
        granola: boolean;
        pacoca: boolean;
        leiteninho: boolean;
    };
};

export const Home = () => {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const [quantidade, setQuantidade] = useState(1);
    const [valorTotal, setValorTotal] = useState(10); // Inicializa com o valor para 'PEQUENO'
    const [etapaAtual, setEtapaAtual] = useState(1); 

    const { register, handleSubmit, watch, setValue, formState: { errors } } = useForm<FormValues>({
        defaultValues: {
            tamanho: 'PEQUENO', // Define 'PEQUENO' como o tamanho padrão
            sabor: 'MORANGO', // Opção para definir um sabor padrão, se necessário
            complementos: {},
        },
        resolver: zodResolver(acaiSchema),
    });

    const tamanho = watch("tamanho");
    const complementosWatch = watch("complementos");

    useEffect(() => {
        // Ajusta o valor base de acordo com o tamanho selecionado
        let baseValor = tamanho === 'PEQUENO' ? 10 : tamanho === 'MEDIO' ? 12 : 15;
        let complementoValor = Object.values(complementosWatch || {}).filter(Boolean).length * 3; // 3 reais para cada complemento selecionado
        let total = (baseValor + complementoValor) * quantidade;

        setValorTotal(total);
    }, [tamanho, complementosWatch, quantidade]);

    const onSubmit: SubmitHandler<FormValues> = data => {
        const complementosSelecionados = Object.entries(data.complementos)
            .filter(([_, value]) => value)
            .map(([key]) => key)
            .join(', ');

        const payload = {
            ...data,
            complemento: complementosSelecionados,
        };

        dispatch(setPedido(payload));
        navigate('/pedido');
    };

    const avancarParaProximaEtapa = () => {
        if (etapaAtual < 3) {
            setEtapaAtual(etapaAtual + 1);
        } else {
            handleSubmit(onSubmit)(); // Invoca onSubmit diretamente
        }
    };

    return (
        <form onSubmit={(e) => e.preventDefault()}>
            <h1>Açaí Natural</h1>

            {etapaAtual === 1 && <RadioTamanho register={register} />}
            {etapaAtual === 2 && <RadioFruta register={register} />}
            {etapaAtual === 3 && <CheckboxComplementos register={register} />}

            <Footer onSubmit={avancarParaProximaEtapa} valorTotal={`R$ ${valorTotal.toFixed(2)}`} quantidade={quantidade} setQuantidade={setQuantidade} />
        </form>
    );
};
