import React, { useState, useEffect } from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { acaiSchema } from '../../validation';
import { useDispatch } from 'react-redux';
import { setPedido } from '../../features/pedido/pedidoSlice';
import { useNavigate } from 'react-router-dom';
import './Home.css';
import Footer from '../../components/Footer/Footer';
import QuantidadeAcai from '../../components/Footer/QuantidadeAcai';
import BotaoAvancar from '../../components/Footer/BotaoAvancar';

type FormValues = {
    sabor: 'MORANGO' | 'BANANA' | 'KIWI';
    tamanho: 'PEQUENO' | 'MEDIO' | 'GRANDE';
    complemento: 'GRANOLA' | 'PAÇOCA' | 'LEITE NINHO' | 'NENHUM';
};

export const Home = () => {

    const navigate = useNavigate();
    const dispatch = useDispatch();
    const { register, handleSubmit, formState: { errors }, watch } = useForm<FormValues>({
        resolver: zodResolver(acaiSchema),
    });
    const [showFooter, setShowFooter] = useState(false);
    const watchFields = watch(["sabor", "tamanho", "complemento"]);
    const onSubmit: SubmitHandler<FormValues> = (data) => {
        dispatch(setPedido(data));
        navigate('/pedido');
    };
    const [valorTotal, setValorTotal] = useState(0);
    const [quantidade, setQuantidade] = useState(1);
    const { sabor, tamanho, complemento } = watch({ sabor: '', tamanho: '', complemento: '' });

    useEffect(() => {
        const allFieldsFilled = [sabor, tamanho, complemento].every(field => field !== "");
        setShowFooter(allFieldsFilled);

        let baseValor = 0;

        // Agora você pode acessar diretamente as variáveis
        switch(tamanho) {
            case 'PEQUENO': baseValor = 10; break;
            case 'MEDIO': baseValor = 12; break;
            case 'GRANDE': baseValor = 15; break;
            default: break;
        }

        const complementoValor = complemento !== 'NENHUM' ? 3 : 0;
        const total = (baseValor + complementoValor) * quantidade;

        setValorTotal(total); 
    }, [sabor, tamanho, complemento, quantidade]);

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <div>
                <h1>Açaí Natural</h1>
                <label>Sabor:</label>
                <select {...register('sabor')}>
                    <option value="">Selecione um sabor</option>
                    <option value="MORANGO">Morango</option>
                    <option value="BANANA">Banana</option>
                    <option value="KIWI">Kiwi</option>
                </select>
                {errors.sabor && <p>O sabor é obrigatório.</p>}
            </div>
            <div>
                <label>Tamanho:</label>
                <select {...register('tamanho')}>
                    <option value="">Selecione um tamanho</option>
                    <option value="PEQUENO">Pequeno (300ml)</option>
                    <option value="MEDIO">Médio (500ml)</option>
                    <option value="GRANDE">Grande (700ml)</option>
                </select>
                {errors.tamanho && <p>O tamanho é obrigatório.</p>}
            </div>
            <div>
                <label>Complemento:</label>
                <select {...register('complemento')}>
                    <option value="">Selecione um complemento</option>
                    <option value="GRANOLA">Granola</option>
                    <option value="PAÇOCA">Paçoca</option>
                    <option value="LEITE NINHO">Leite Ninho</option>
                    <option value="NENHUM">Nenhum</option>
                </select>
                {errors.complemento && <p>O complemento é obrigatório.</p>}
            </div>
            <div className="desktop-footer" id="footer">
                <QuantidadeAcai quantidade={quantidade} setQuantidade={setQuantidade} />
                <BotaoAvancar valorTotal={`R$ ${valorTotal.toFixed(2)}`} onSubmit={() => handleSubmit(onSubmit)()} />
            </div>

            {showFooter && (
                <Footer 
                    onSubmit={() => handleSubmit(onSubmit)()} 
                    valorTotal={`R$ ${valorTotal.toFixed(2)}`} 
                    quantidade={quantidade}
                    setQuantidade={setQuantidade}
                />
            )}
        </form>
    );
};
