import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { acaiSchema } from '../../validation';
import { useDispatch } from 'react-redux';
import { setPedido } from '../../features/pedido/pedidoSlice';
import { useNavigate } from 'react-router-dom';
import { fetchProdutos } from '../../services/mockApi';
import { Produto } from '../../features/types/index';
import RadioTamanho from '../../components/Home/RadioTamanho';
import RadioFruta from '../../components/Home/RadioFruta';
import CheckboxComplementos from '../../components/Home/CheckboxComplementos';
import Footer from '../../components/Footer/Footer';
import BotaoVoltar from '../../components/BotaoVoltar';
import produtoImg from '../../assets/img/Banner.png';
import '../../components/Home/Home.css';

type FormValues = {
    tamanho: string;
    sabor: string;
    complementos: Record<string, boolean>;
};

export const Home = () => {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const [quantidade, setQuantidade] = useState(1);
    const [valorTotal, setValorTotal] = useState(0); // Valor inicial arbitrário
    const [etapaAtual, setEtapaAtual] = useState(1);
    const [tamanhos, setTamanhos] = useState<Produto[]>([]);
    const [sabores, setSabores] = useState<Produto[]>([]);
    const [complementos, setComplementos] = useState<Produto[]>([]);

    const { register, handleSubmit, watch, formState: { errors } } = useForm<FormValues>({
        resolver: zodResolver(acaiSchema),
    });

    useEffect(() => {
        const loadData = async () => {
            const produtosData: Produto[] = await fetchProdutos();
            setTamanhos(produtosData.filter(produto => produto.tipo === 'tamanho'));
            setSabores(produtosData.filter(produto => produto.tipo === 'sabor'));
            setComplementos(produtosData.filter(produto => produto.tipo === 'complemento'));
        };

        loadData();
    }, []);

    // Assista as mudanças nos valores relevantes usando watch
    const watchTamanho = watch('tamanho');
    const watchComplementos = watch('complementos');

    // Recalcule o valor total sempre que os valores observados mudarem
    useEffect(() => {
        onSubmitData({tamanho: watchTamanho, sabor: watch('sabor'), complementos: watchComplementos}, false);
    }, [watchTamanho, watchComplementos, quantidade]);

    const onSubmitData = (data: FormValues, shouldNavigate: boolean = true) => {
        let totalValor = 0;
        const tamanhoSelecionado = tamanhos.find(t => t.nome === data.tamanho);
        if (tamanhoSelecionado && tamanhoSelecionado.valor) {
            totalValor += parseFloat(tamanhoSelecionado.valor.replace(',', '.'));
        }

        Object.entries(data.complementos).forEach(([key, isSelected]) => {
            if (isSelected) {
                const complementoSelecionado = complementos.find(c => c.nome.toLowerCase() === key.toLowerCase());
                if (complementoSelecionado && complementoSelecionado.valor) {
                    totalValor += parseFloat(complementoSelecionado.valor.replace(',', '.'));
                }
            }
        });

        totalValor *= quantidade;
        setValorTotal(totalValor); // Atualize o valorTotal diretamente

        if (shouldNavigate) {
            dispatch(setPedido({
                tamanho: data.tamanho,
                sabor: data.sabor,
                complementos: Object.keys(data.complementos).filter(k => data.complementos[k]),
                valorTotal: totalValor, // Atualizado para usar o valor calculado
            }));
            navigate('/pedido');
        }
    };

    const avancarParaProximaEtapa = () => {
        if (etapaAtual < 3) {
            setEtapaAtual(etapaAtual + 1);
        } else {
            handleSubmit((data) => onSubmitData(data))();
        }
    };

    const voltarEtapa = () => {
        if (etapaAtual > 1) {
            setEtapaAtual(etapaAtual - 1);
        }
    };

    return (
        <section>
            <div id='produto' className='container'>
                <BotaoVoltar onClick={voltarEtapa} />
                <img src={produtoImg} alt="produto açaí" />
            </div>
            <form className='container' onSubmit={handleSubmit(onSubmitData)}>
                {etapaAtual === 1 && <RadioTamanho register={register} tamanhos={tamanhos} />}
                {etapaAtual === 2 && <RadioFruta register={register} sabores={sabores} />}
                {etapaAtual === 3 && <CheckboxComplementos register={register} complementos={complementos} />}
                <Footer
                    onSubmit={avancarParaProximaEtapa}
                    valorTotal={`R$ ${valorTotal.toFixed(2)}`}
                    quantidade={quantidade}
                    setQuantidade={setQuantidade}
                />
            </form>
        </section>
    );
};
