import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import { RootState } from '../../app/store';

interface PedidoState {
  tamanho: 'PEQUENO' | 'MEDIO' | 'GRANDE';
  frutas: string;
  complemento: string;
  valorTotal: string; // Adicione esta linha
}

const initialState: PedidoState = {
  tamanho: 'PEQUENO', // valor padrão de exemplo
  frutas: '', // valor padrão de exemplo
  complemento: '', // valor padrão de exemplo
  valorTotal: '0', // valor padrão de exemplo
}

export const pedidoSlice = createSlice({
  name: 'pedido',
  initialState,
  reducers: {
    setPedido: (state, action: PayloadAction<PedidoState>) => {
      state.frutas = action.payload.frutas;
      state.tamanho = action.payload.tamanho;
      state.complemento = action.payload.complemento;
    },
  },
});

export const { setPedido } = pedidoSlice.actions;

export const selectPedido = (state: RootState) => state.pedido;

export default pedidoSlice.reducer;
