import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { fetchProdutos } from '../../services/mockApi';
import RadioTamanho from '../../components/Home/RadioTamanho';
import RadioFruta from '../../components/Home/RadioFruta';
import CheckboxComplementos from '../../components/Home/CheckboxComplementos';
import Footer from '../../components/Footer/Footer';
import BotaoVoltar from '../../components/BotaoVoltar';
import produtoImg from '../../assets/img/Banner.png';
import '../../components/Home/Home.css';

// Supondo que o tipo Produto esteja definido corretamente em './types'
import { Produto } from './types';

type FormValues = {
  tamanho: string;
  sabor: string;
  complementos: Record<string, boolean>;
};

export const Home = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [quantidade, setQuantidade] = useState(1);
  const [valorTotal, setValorTotal] = useState(0);
  const [etapaAtual, setEtapaAtual] = useState(1);
  const [produtos, setProdutos] = useState<Produto[]>([]);

  const { register, handleSubmit, watch, formState: { errors }, setValue } = useForm<FormValues>({
    defaultValues: {
      tamanho: '',
      sabor: '',
      complementos: {}
    }
  });

  useEffect(() => {
    const loadData = async () => {
      const produtosData: Produto[] = await fetchProdutos();
      setProdutos(produtosData);
      // Inicializar valores padrões para tamanho e sabor aqui se necessário
    };

    loadData();
  }, []);

  const calculateAndUpdateTotal = (values: FormValues) => {
    let total = 0;
    // Adicione aqui a lógica de cálculo do total baseada em values e quantidade
    setValorTotal(total);
  };

  const onSubmitData = (data: FormValues) => {
    // Faça aqui a lógica de submissão dos dados
    dispatch(setPedido({
      tamanho: data.tamanho,
      sabor: data.sabor,
      complementos: Object.keys(data.complementos).filter(k => data.complementos[k]),
      valorTotal: valorTotal.toString(),
    }));
    navigate('/pedido');
  };

  useEffect(() => {
    const subscription = watch((values) => {
      calculateAndUpdateTotal(values);
    });
    return () => subscription.unsubscribe();
  }, [watch]);

  return (
    <section>
        <div id='produto' className='container'>
            <BotaoVoltar onClick={() => navigate(-1)} />
            <img src={produtoImg} alt="Açaí" />
        </div>
        <form className='container' onSubmit={handleSubmit(onSubmitData)}>
            {etapaAtual === 1 && <RadioTamanho register={register} tamanhos={produtos.filter(p => p.tipo === 'tamanho')} />}
            {etapaAtual === 2 && <RadioFruta register={register} sabores={produtos.filter(p => p.tipo === 'sabor')} />}
            {etapaAtual === 3 && <CheckboxComplementos register={register} complementos={produtos.filter(p => p.tipo === 'complemento')} />}
            <Footer onSubmit={() => onSubmitData(getValues())} valorTotal={`R$ ${valorTotal.toFixed(2)}`} quantidade={quantidade} setQuantidade={setQuantidade} />
        </form>
    </section>
  );
};
