import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { acaiSchema } from '../../validation';
import { useDispatch } from 'react-redux';
import { setPedido } from '../../features/pedido/pedidoSlice';
import { useNavigate } from 'react-router-dom';
import { fetchProdutos } from '../../services/mockApi';
import RadioTamanho from '../../components/Home/RadioTamanho';
import RadioFruta from '../../components/Home/RadioFruta';
import CheckboxComplementos from '../../components/Home/CheckboxComplementos';
import Footer from '../../components/Footer/Footer';
import BotaoVoltar from '../../components/BotaoVoltar';
import produtoImg from '../../assets/img/Banner.png';
import '../../components/Home/Home.css';

// Definindo o tipo de produto conforme esperado da API
interface Produto {
  nome: string;
  tipo: 'tamanho' | 'sabor' | 'complemento';
  valor?: string; // Assume que valor pode ser opcional
}

type FormValues = {
    tamanho: string;
    sabor: string;
    complementos: Record<string, boolean>;
};

export const Home = () => {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const [quantidade, setQuantidade] = useState(1);
    const [valorTotal, setValorTotal] = useState<number>(0);
    const [etapaAtual, setEtapaAtual] = useState(1);
    const [produtos, setProdutos] = useState<Produto[]>([]);

    const { register, handleSubmit, watch, setValue, getValues } = useForm<FormValues>({
        resolver: zodResolver(acaiSchema),
    });

    // Função para calcular e atualizar o valor total
    const calculateAndUpdateTotal = () => {
        const currentValues = getValues();
        let totalValor = 0;
        // Implementação do cálculo do valor total com base nos valores atuais
        // e a quantidade de produtos
        setValorTotal(totalValor);
    };

    useEffect(() => {
        const loadData = async () => {
            const data = await fetchProdutos();
            setProdutos(data);
            // Inicializa o formulário com os valores padrões
            if (data.length > 0) {
                const tamanhoInicial = data.find(p => p.tipo === 'tamanho')?.nome || '';
                setValue('tamanho', tamanhoInicial);
                calculateAndUpdateTotal();
            }
        };

        loadData();
    }, []);

    useEffect(() => {
        const subscription = watch(() => {
            calculateAndUpdateTotal();
        });
        return () => subscription.unsubscribe();
    }, [watch]);

    useEffect(() => {
        calculateAndUpdateTotal();
    }, [quantidade]);

    const onSubmitData = handleSubmit((data: FormValues) => {
        dispatch(setPedido({
            ...data,
            valorTotal: valorTotal.toString(),
        }));
        navigate('/pedido');
    });

    return (
        <section>
            <div id='produto' className='container'>
                <BotaoVoltar onClick={() => navigate(-1)} />
                <img src={produtoImg} alt="produto açaí" />
            </div>
            <form className='container' onSubmit={onSubmitData}>
                {/* Renderização condicional dos componentes com props apropriadas */}
                <Footer
                    onSubmit={avancarParaProximaEtapa}
                    valorTotal={`R$ ${valorTotal.toFixed(2)}`}
                    quantidade={quantidade}
                    setQuantidade={setQuantidade}
                />
            </form>
        </section>
    );
};
