import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { acaiSchema } from '../../validation';
import { useDispatch } from 'react-redux';
import { setPedido } from '../../features/pedido/pedidoSlice';
import { useNavigate } from 'react-router-dom';
import { fetchTamanhos, fetchSabores } from '../../services/mockApi';
import Footer from '../../components/Footer/Footer';
import RadioTamanho from '../../components/Home/RadioTamanho';
import RadioFruta from '../../components/Home/RadioFruta';
import CheckboxComplementos from '../../components/Home/CheckboxComplementos';
import BotaoVoltar from '../../components/BotaoVoltar';
import produto from '../../assets/img/Banner.png';
import '../../components/Home/Home.css';

type FormValues = {
    tamanho: 'PEQUENO' | 'MEDIO' | 'GRANDE';
    sabor: 'MORANGO' | 'BANANA' | 'KIWI';
    complementos: {
        granola: boolean;
        pacoca: boolean;
        leiteninho: boolean;
    };
};

export const Home = () => {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const [quantidade, setQuantidade] = useState(1);
    const [valorTotal, setValorTotal] = useState(10); // Inicializa com o valor para 'PEQUENO'
    const [etapaAtual, setEtapaAtual] = useState(1); // Controle de etapas para o "slider"
    const [tamanhos, setTamanhos] = useState<string[]>([]);
    const [sabores, setSabores] = useState<string[]>([]);

    const { register, handleSubmit, watch, formState: { errors } } = useForm<FormValues>({
        defaultValues: {
            tamanho: 'PEQUENO',
            sabor: undefined, // Corrigindo para 'undefined'
            complementos: {},
        },
        resolver: zodResolver(acaiSchema),
    });

    useEffect(() => {
        const loadData = async () => {
            const tamanhosData = await fetchTamanhos();
            const saboresData = await fetchSabores();
            setTamanhos(tamanhosData.map((tamanho: string) => tamanho.toUpperCase())); // Tipo explicitamente definido
            setSabores(saboresData.map((sabor: string) => sabor.toUpperCase())); // Tipo explicitamente definido
        };

        loadData();
    }, []);

    const tamanho = watch("tamanho");
    const sabor = watch("sabor");
    const complementos = watch("complementos");

    useEffect(() => {
        let baseValor = tamanho === 'PEQUENO' ? 10 : tamanho === 'MEDIO' ? 12 : 15;
        let complementoValor = 0;

        // Lógica de cálculo do valor com base nos complementos selecionados
        let total = (baseValor + complementoValor) * quantidade;
        setValorTotal(total);
    }, [tamanho, quantidade, complementos]);

    const onSubmitData = (data: FormValues) => {
        dispatch(setPedido({
            ...data,
            complemento: '', // Adicionando um complemento padrão
            valorTotal: valorTotal.toFixed(2),
        }));
        navigate('/pedido');
    };

    const avancarParaProximaEtapa = () => {
        if (etapaAtual < 3) {
            setEtapaAtual(etapaAtual + 1);
        } else {
            handleSubmit(onSubmitData)();
        }
    };

    const voltarEtapa = () => {
        if (etapaAtual > 1) {
            setEtapaAtual(etapaAtual - 1);
        }
    };

    return (
        <section>
            <div id='produto' className='container'>
                <BotaoVoltar onClick={voltarEtapa} />
                <img src={produto} alt="produto açaí" />
            </div>
            <div id='descricao' className='container'>
                <h1>Açaí Natural</h1>
                <div id='review'>
                    <span id='star_icon'></span>
                    <b>4.5</b>
                    <p>(30+)</p>
                    <a href="#">Ver Avaliações</a>
                </div>
                </div>
            <form className='container' onSubmit={handleSubmit(onSubmitData)}>
                {etapaAtual === 1 && <RadioTamanho register={register} tamanhos={tamanhos} />}
                {etapaAtual === 2 && <RadioFruta register={register} sabores={sabores} />}
                {etapaAtual === 3 && <CheckboxComplementos register={register} />}
                <Footer
                    onSubmit={avancarParaProximaEtapa}
                    valorTotal={`R$ ${valorTotal.toFixed(2)}`}
                    quantidade={quantidade}
                    setQuantidade={setQuantidade}
                />
            </form>
        </section>
    );
};
