import React, { useState, useEffect } from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { acaiSchema } from '../../validation';
import { useDispatch } from 'react-redux';
import { setPedido } from '../../features/pedido/pedidoSlice';
import { useNavigate } from 'react-router-dom';
import Footer from '../../components/Footer/Footer';
import RadioTamanho from '../../components/Home/RadioTamanho';
import RadioFruta from '../../components/Home/RadioFruta';
import CheckboxComplementos from '../../components/Home/CheckboxComplementos';
import './Home.css';

type FormValues = {
    tamanho: 'PEQUENO' | 'MEDIO' | 'GRANDE';
    sabor: 'MORANGO' | 'BANANA' | 'KIWI';
    complementos: {
        granola: boolean;
        pacoca: boolean;
        leiteninho: boolean;
    };
};

export const Home = () => {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const [quantidade, setQuantidade] = useState(1);
    const [valorTotal, setValorTotal] = useState(0);
    const { register, handleSubmit, watch, setValue, formState: { errors } } = useForm<FormValues>({
        defaultValues: {
            tamanho: 'PEQUENO', // Valor inicial para o tamanho
            sabor: 'MORANGO', // Valor inicial para o sabor, caso deseje
            complementos: {},
        },
        resolver: zodResolver(acaiSchema),
    });

    const complementos = watch("complementos");
    const [etapaAtual, setEtapaAtual] = useState(1); // 1 para tamanho, 2 para sabor, 3 para complementos

    useEffect(() => {
        let baseValor = 10; // Valor para PEQUENO como padrão
        const complementoValor = Object.values(complementos || {}).filter(Boolean).length * 3;
        const total = (baseValor + complementoValor) * quantidade;

        setValorTotal(total);
    }, [complementos, quantidade]);

    useEffect(() => {
        setValue("tamanho", "PEQUENO");
    }, [setValue]);

    const onSubmit: SubmitHandler<FormValues> = (data) => {
        const complementosSelecionados = Object.entries(data.complementos)
            .filter(([_, value]) => value)
            .map(([key]) => key)
            .join(', ');

        const payload = {
            ...data,
            complemento: complementosSelecionados,
        };

        dispatch(setPedido(payload));
        navigate('/pedido');
    };

    return (
        <form onSubmit={(e) => e.preventDefault()}>
            <h1>Açaí Natural</h1>

            {etapaAtual === 1 && <RadioTamanho register={register} />}
            {etapaAtual === 2 && <RadioFruta register={register} />}
            {etapaAtual === 3 && <CheckboxComplementos register={register} />}

            <Footer onSubmit={() => avancarParaProximaEtapa()} valorTotal={`R$ ${valorTotal.toFixed(2)}`} quantidade={quantidade} setQuantidade={setQuantidade} />
        </form>
    );
};

const avancarParaProximaEtapa = () => {
    if (etapaAtual < 3) {
        setEtapaAtual(etapaAtual + 1);
    } else {
        // Aqui, submeter o formulário ou navegar para a próxima página
        handleSubmit(onSubmit)();
    }
};
